void draw_text(uint16_t x, uint16_t y, uint16_t size, uint16_t color, uint8_t *text) {
    uint8_t *draw_char;
    const uint8_t *font;

    uint16_t x_curr = x;
    uint16_t y_curr = y;

    uint16_t target;

    switch (size) {
        case 16:
              font = CGA16;
              break;
        case 32:
            font = jmk32;
            break;
        default:
              font = CGA16;
    }

    for (uint16_t i = 0; i < strlen(text); i++) {

        if (x_curr >= SCREEN_WIDTH - size) {
            x_curr = x;
            y_curr += size;
        }

        if (y_curr >= SCREEN_HEIGHT - size) {
            return;
        }


        target = (size*size >> 3) * (text[i] - ' ');
        draw_char = font + target;

        draw_bitmap(x_curr, y_curr, size, size, color, draw_char);

        x_curr += size;
    }
}


void draw_text_bg(uint16_t x, uint16_t y, uint16_t size, uint16_t fg, uint16_t bg,
                  const char *text)
{
    const uint8_t *font;
    uint16_t x_curr = x;
    uint16_t y_curr = y;

    switch (size) {
        case 16:
            font = CGA16;
            break;
        case 32:
            font = jmk32;
            break;
        default:
            font = CGA16;
            break;
    }

    for (uint16_t i = 0; text[i] != '\0'; i++) {
        if (x_curr >= SCREEN_WIDTH - size) {
            x_curr = x;
            y_curr += size;
        }
        if (y_curr >= SCREEN_HEIGHT - size) return;

        char ch = text[i];
        if (ch < ' ' || ch > '~') ch = '?';  // guard

        uint16_t target = (size * size >> 3) * (uint16_t)(ch - ' ');
        const uint8_t *draw_char = font + target;

        draw_bitmap_bg(x_curr, y_curr, size, size, fg, bg, draw_char);

        x_curr += size;
    }
}

void draw_text_center(uint16_t y, uint16_t size, uint16_t color, uint8_t *text) {
    uint16_t text_width = strlen((char*)text) * size;
    uint16_t x = (SCREEN_WIDTH - text_width) / 2;
    draw_text(x, y, size, color, text);
}

void draw_text_center_bg(uint16_t y, uint16_t size, uint16_t text_color, uint16_t bg, uint8_t *text) {
    uint16_t text_width = strlen((char*)text) * size;
    uint16_t x = (SCREEN_WIDTH - text_width) / 2;
    draw_text_bg(x, y, size, text_color, bg, text);
}




#define FONT_W 16
#define FONT_H 16
#define LINE_H 16          

#define MAX_COLS 16
#define MAX_LINES 10       
#define MAX_LINE_CHARS 64  

static int wrap_words_fixed16(const char *text, char lines[MAX_LINES][MAX_LINE_CHARS]) {
    int line = 0;
    int col  = 0;

    while (*text && line < MAX_LINES) {
        while (*text == ' ') text++;

        if (!*text) break;

        const char *word = text;
        int wlen = 0;
        while (word[wlen] && word[wlen] != ' ') wlen++;

        if (col > 0 && (col + 1 + wlen) > MAX_COLS) {
            lines[line][col] = '\0';
            line++;
            col = 0;
            if (line >= MAX_LINES) break;
        }

        if (col == 0 && wlen > MAX_COLS) {
            int take = MAX_COLS;
            memcpy(&lines[line][col], text, (size_t)take);
            col += take;
            text += take;
            lines[line][col] = '\0';
            line++;
            col = 0;
            continue;
        }

        // add space if needed
        if (col > 0) lines[line][col++] = ' ';

        // copy word
        memcpy(&lines[line][col], text, (size_t)wlen);
        col += wlen;
        text += wlen;
    }

    if (line < MAX_LINES && col > 0) {
        lines[line][col] = '\0';
        line++;
    }

    return line; 
}

void draw_quote_centered(const char *quote, uint16_t color) {
    char lines[MAX_LINES][MAX_LINE_CHARS] = {0};
    int n = wrap_words_fixed16(quote, lines);

    int block_h = n * LINE_H;
    int start_y = (SCREEN_HEIGHT - block_h) / 2;

    for (int i = 0; i < n; i++) {
        int len = (int)strlen(lines[i]);
        int line_w_px = len * FONT_W;

        int x = (SCREEN_WIDTH - line_w_px) / 2;
        int y = start_y + i * LINE_H;

        draw_text(x, y, 16, color, lines[i]);   
    }
}





