#include "text.h"
#include "util.h"
#include "image.h"
#include <string.h>



void draw_text(uint16_t x, uint16_t y, uint16_t size, uint16_t color, uint8_t *text) {
    uint8_t *draw_char;
    const uint8_t *font;

    uint16_t x_curr = x;
    uint16_t y_curr = y;

    uint16_t target;

    switch (size) {
        case 16:
        default:
              font = CGA16;
    }

    for (uint16_t i = 0; i < strlen(text); i++) {

        if (x_curr >= SCREEN_WIDTH - size) {
            x_curr = x;
            y_curr += size;
        }

        if (y_curr >= SCREEN_HEIGHT - size) {
            return;
        }


        target = (size*size >> 3) * (text[i] - ' ');
        draw_char = font + target;

        draw_bitmap(x_curr, y_curr, size, size, color, draw_char);

        x_curr += size;
    }
}


void draw_text_bg(uint16_t x, uint16_t y, uint16_t size, uint16_t fg, uint16_t bg,
                  const char *text)
{
    const uint8_t *font;
    uint16_t x_curr = x;
    uint16_t y_curr = y;

    switch (size) {
        case 16:
        default:
            font = CGA16;
            break;
    }

    for (uint16_t i = 0; text[i] != '\0'; i++) {
        if (x_curr >= SCREEN_WIDTH - size) {
            x_curr = x;
            y_curr += size;
        }
        if (y_curr >= SCREEN_HEIGHT - size) return;

        char ch = text[i];
        if (ch < ' ' || ch > '~') ch = '?';  // guard

        uint16_t target = (size * size >> 3) * (uint16_t)(ch - ' ');
        const uint8_t *draw_char = font + target;

        draw_bitmap_bg(x_curr, y_curr, size, size, fg, bg, draw_char);

        x_curr += size;
    }
}

void draw_text_center(uint16_t y, uint16_t size, uint16_t color, uint8_t *text) {
    uint16_t text_width = strlen((char*)text) * size;
    uint16_t x = (SCREEN_WIDTH - text_width) / 2;
    draw_text(x, y, size, color, text);
}

void draw_text_center_bg(uint16_t y, uint16_t size, uint16_t text_color, uint16_t bg, uint8_t *text) {
    uint16_t text_width = strlen((char*)text) * size;
    uint16_t x = (SCREEN_WIDTH - text_width) / 2;
    draw_text(x, y, size, text_color, bg, color);
}






